/* free_ipCountersTable */
/**
* @details 
*          This is the routine which frees the mac counters entry.
*/
void free_ipCountersTable(void)
{
  ipCountersTableEntry  * current = ipCountersTable_head;
  ipCountersTableEntry  * temp    = NULL; 	

  if(current)
  {
    while(current->next != NULL)
    {
        temp = current->next;
        SNMP_FREE( current );
        current = temp;
    }

    SNMP_FREE( current );
    ipCountersTable_head = NULL;
  }
  return;
}


