    iinfo->get_first_data_point = qosConfigTable_get_first_data_point;
    iinfo->get_next_data_point  = qosConfigTable_get_next_data_point;
    iinfo->table_reginfo        = table_info;
    
    netsnmp_register_table_iterator( reg, iinfo );

    /* Initialise the contents of the table here */
